import { useState,useEffect } from 'react'
import Container from './../Ui/Container.jsx'
const Figure = (props) => {
  const [memesArray, setMemesArray] = useState(null)
  const [src, setSrc] = useState(null)

 const fetchApi =  async () => {
   const url = 'https://meme-api.com/gimme/100'
  try {
    const response = await fetch(url);
    let data = await response.json();
    setMemesArray(data)
  } catch (error) {
    alert(error);
  }

  const getRandomUrl = () => {
    for(let i = 0;i < memesArray.memes.length;i++){
      let randomUrl = Math.floor(Math.random() * i)
      setSrc(memesArray.memes[randomUrl].url)
    }
  }

  useEffect(() => {
    fetchApi()
  },[src])
}
  return(
    <div className='figure--section w-full h-full'>
    <Container>
    <figure className='w-full h-34 px-2 relative mx-auto mt-14 relative text-center'>
     <span className='meme--text absolute -translate-x-1/2 text-4xl text-white font-bold top-5'>
    {props.Toptxt}
    </span>
     <span className='meme--text absolute -translate-x-1/2 text-4xl text-white font-bold bottom-5'>
    {props.Bottomtxt}
    </span>
    <img className=' w-full h-full object-cover rounded-md' src={src} alt='meme'/>
    </figure>
    </Container>
    </div>
    )
}
export default Figure;
