import { useState,useEffect } from 'react'
import Container from './../Ui/Container.jsx'
const Figure = ({data}) => {
  const url = 'https://api.imgflip.com/get_memes';

  const [imageData, setImageData] = useState(null);

  const fetchImage = async () => {
    const response = await fetch(url);
    const data = await response.json();
    setImageData(data);
  }
  useEffect(() => {
    // This will run when the form is submitted
    fetchImage();
  }, [props.data]);
  return(
    <div className='figure--section w-full h-full'>
    <Container>
    <figure className='w-full h-34 px-2 relative mx-auto mt-14 relative text-center'>
     <span className='meme--text absolute -translate-x-1/2 text-4xl text-white font-bold top-5'>
    {props.Toptxt}
    </span>
     <span className='meme--text absolute -translate-x-1/2 text-4xl text-white font-bold bottom-5'>
    {props.Bottomtxt}
    </span>
    <img className=' w-full h-full object-cover rounded-md' src={imageData.memes[0].url} alt='meme'/>
    </figure>
    </Container>
    </div>
    )
}
export default Figure;
