import Container from './../Ui/Container.jsx'
import { useRef,useEffect } from 'react'
import Logo from './../Media/meme.png'
const Navbar = () => {
  const checkbox = useRef()
  const HTML = document.documentElelment
  useEffect(() => {
   const checkTheme = () => {
     if(checkbox.current.checked){
       HTML.classList.add('dark')
     }
   }
   checkTheme()
  },[])
  return (
  <header className='w-full h-16 relative bg-gradient-to-r from-sky-500 to-indigo-500 dark:from-pink-600 dark:to-pink-900'>
  <Container>
  <nav className='h-full flex justify-between px-2 items-center'>
  <a href="#home" className='flex items-center gap-1'>
  <img className='w-12 sm:w-14 md:w-16' src={Logo} alt='logo'/> 
  <span className='text-xl lg:text-2xl font-bold text-white'>MemeGenerator</span>
  </a>
  <div class="toggle">
    <input ref={checkbox} type="checkbox"/>
    <label></label>
</div>
  </nav>
  </Container>
  </header>
  )
}
export default Navbar;
