import { useState,useEffect } from 'react'
import Container from './../Ui/Container.jsx'
const Figure = (props) => {
  const [data, setData] = useState(null) ;
const [img, setImg] = useState(null);

useEffect (() => {
async function fetchData () {
try {
const response = await fetch
('https://api.imgflip.com/get_memes');
const data = await response.json();
setData(data) ;
for(let i = 0; i < data.data.memes.length; i++) {
const randomUrl = Math.floor(Math.random() * i);
setImg(data.data.memes[randomUrl].url);
}
} catch(e){
alert(e);
}
}
fetchData()
}, []);
  return(
    <div className='figure--section w-full h-full'>
    <Container>
    <figure className='w-full h-34 px-2 relative mx-auto mt-12 mb-8 relative text-center'>
     {data ? <> <span className='meme--text absolute -translate-x-1/2 text-3xl text-white font-bold top-4 w-34'>
    {props.Toptxt}
    </span>
     <span className='meme--text absolute -translate-x-1/2 text-3xl text-white font-bold bottom-4 w-34'>
    {props.Bottomtxt}
    </span>
    <img className=' w-full h-full object-cover rounded-md max-h-96' src={img} Loading="lazy" alt='meme'/> </> : <span>Loading</span> }
    </figure> 
    <div className="w-full relative flex justify-">
     <button onClick={() => {
      for(let j = 0; j < data.data.memes.length;j++){
        setImg(data.data.memes[Math.floor(Math.random() * j)].url)
      }
    }} className="mt-5 px-2 px-3 border border-sky-500 bg-gradient-to-r from-sky-500 to-indigo-500 focus:ring-1 focus:ring-sky-500 focus:border-white w-full py-2 text-white font-bold tracking-wide rounded-md">Generate new one </button>
    </div>
    </Container>
    </div>
    )
}
export default Figure;
