import { useState } from 'react'
import Container from './../Ui/Container.jsx'
const Form = () => {
  const [valSt, setValSt] = useState('Shut up')
  const [valNd, setValNd] = useState('and take my money')
  const submitHandler = e => {
    e.preventDefault();
    setValSt('Shut up')
  }
  return(
    <div className='form--section'>
    <Container>
    <form className='flex flex-col relative px-2 gap-5 w-full mt-8' onSubmit={submitHandler}>
    <div className='group--1 flex w-full items-center basis-[45%] gap-2'>
    <input onChange={e => setValSt(e.target.value)} value={valSt} type="text" className="mt-1 block w-full px-3 py-2 bg-white border border-slate-300 rounded-md text-sm text-slate-700 shadow-sm
      focus:outline-none focus:border-sky-500 focus:ring-1 focus:ring-sky-500
   rounded-md tracking-wide"/>
    <input onChange={e => setValNd(e.target.value)} value={valNd} type="text" className="mt-1 block w-full px-3 py-2 bg-white border border-slate-300 rounded-md text-sm text-slate-700 tracking-wide shadow-sm
      focus:outline-none focus:border-sky-500 focus:ring-1 focus:ring-sky-500
   rounded-md"/>
    </div>
    <button type='submit' className='border border-sky-500 bg-gradient-to-r from-sky-500 to-indigo-500 focus:ring-1 focus:ring-sky-500 focus:border-white w-full py-2 text-white font-bold tracking-wide rounded-md'>Get random meme
   </button>
    </form>
    </Container>
    </div>
    )
}
export default Form;
